<script>
    function navigatePage(pageNumber) {
        // Lấy URL hiện tại của trang
        var currentUrl = window.location.href;

        // Xóa bất kỳ tham số pg nào hiện có trong URL để tránh trùng lặp
        var cleanUrl = currentUrl.replace(/([?&]pg=\d+)/, '');

